section.code:
httpd_service() -> {httpd, httpd()}
httpd()         -> [httpd_config()] 
httpd_config()  -> {proplist_file, file()}
                   {debug, debug()} |
                   {accept_timeout, integer()}
debug()         -> disable | [debug_options()]
debug_options() -> {all_functions, modules()} | 
                   {exported_functions, modules()} |
                   {disable, modules()}
modules()       -> [atom()]
