%% Basically send and receive on an IPv4 SCTP (stream) socket
%% using sendmsg and recvmsg.
api_b_sendmsg_and_recvmsg_stream_sctp4(_Config) when is_list(_Config) ->
    ?TT(?SECS(5)),
    tc_try(?FUNCTION_NAME,
           fun() ->
		   has_support_sctp(),
                   has_support_ipv4()%% ,
		   %% not_yet_implemented()
	   end,
           fun() ->
                   Send = fun(Sock, Data) ->
                                  %% CMsg  = #{level => sctp,
                                  %%              type  => tos,
                                  %%              data  => reliability},
                                  %% CMsgs = [CMsg],
                                  Msg = #{%% ctrl => CMsgs,
                                          iov  => [Data]},
                                  socket:sendmsg(Sock, Msg)
                          end,
                   Recv = fun(Sock) ->
                                  case socket:recvmsg(Sock) of
                                      {ok, #{flags := [eor],
                                             addr  := _,
                                             iov   := [Data]}} ->
                                          {ok, Data};
                                      {ok, Msg} ->
                                          {error, {msg, Msg}};
                                      %% {ok, #{addr  := Source,
                                      %%        iov   := [Data]}} ->
                                      %%     {ok, {Source, Data}};
                                      {error, _} = ERROR ->
                                          ERROR
                                  end
                          end,
                   InitState = #{domain => inet,
                                 type   => stream,
                                 proto  => sctp,
                                 send   => Send,
                                 recv   => Recv},
                   %% ok = api_b_send_and_recv_sctp(InitState)
                   ok = api_b_send_and_recv_conn(InitState)
           end).



