section.code:
1> ssl:start(). ok
2> {ok, C1} = ssl:connect("localhost", 9999, [{verify, verify_peer}, {versions, ['tlsv1.2']}, {cacertfile, "cacerts.pem"}]). {ok,{sslsocket,{gen_tcp,#Port<0.7>,tls_connection,undefined}, ...}}
3> ssl:connection_information(C1, [session_id]). {ok,[{session_id,<<95,32,43,22,35,63,249,22,26,36,106, 152,49,52,124,56,130,192,137,161, 146,145,164,232,...>>}]}
%% Reuse session if possible, note that if C2 is really fast the session %% data might not be available for reuse. 4> {ok, C2} = ssl:connect("localhost", 9999, [{verify, verify_peer}, {versions, ['tlsv1.2']}, {cacertfile, "cacerts.pem"}, {reuse_sessions, true}]). {ok,{sslsocket,{gen_tcp,#Port<0.8>,tls_connection,undefined}, ...]}}
%% C2 got same session ID as client one, session was automatically reused. 5> ssl:connection_information(C2, [session_id]). {ok,[{session_id,<<95,32,43,22,35,63,249,22,26,36,106, 152,49,52,124,56,130,192,137,161, 146,145,164,232,...>>}]}

%% We want save this particular session for reuse although it has the same basis as C1 6> {ok, C3} = ssl:connect("localhost", 9999, [{verify, verify_peer}, {versions, ['tlsv1.2']}, {cacertfile, "cacerts.pem"}, {reuse_sessions, save}]). {ok,{sslsocket,{gen_tcp,#Port<0.9>,tls_connection,undefined}, ...]}}
%% A full handshake is performed and we get a new session ID 7> {ok, [{session_id, ID}]} = ssl:connection_information(C3, [session_id]). {ok,[{session_id,<<91,84,27,151,183,39,84,90,143,141, 121,190,66,192,10,1,27,192,33,95,78, 8,34,180,...>>}]}
%% Use automatic session reuse 8> {ok, C4} = ssl:connect("localhost", 9999, [{verify, verify_peer}, {versions, ['tlsv1.2']}, {cacertfile, "cacerts.pem"}, {reuse_sessions, true}]). {ok,{sslsocket,{gen_tcp,#Port<0.10>,tls_connection, undefined}, ...]}}
%% The "saved" one happened to be selected, but this is not a guarantee 9> ssl:connection_information(C4, [session_id]). {ok,[{session_id,<<91,84,27,151,183,39,84,90,143,141, 121,190,66,192,10,1,27,192,33,95,78, 8,34,180,...>>}]}
%% Make sure to reuse the "saved" session 10> {ok, C5} = ssl:connect("localhost", 9999, [{verify, verify_peer}, {versions, ['tlsv1.2']}, {cacertfile, "cacerts.pem"}, {reuse_session, ID}]). {ok,{sslsocket,{gen_tcp,#Port<0.11>,tls_connection, undefined}, ...]}}
11> ssl:connection_information(C5, [session_id]). {ok,[{session_id,<<91,84,27,151,183,39,84,90,143,141, 121,190,66,192,10,1,27,192,33,95,78, 8,34,180,...>>}]}

%% Preform a full handshake and the session will not be saved for reuse 12> {ok, C9} = ssl:connect("localhost", 9999, [{verify, verify_peer}, {versions, ['tlsv1.2']}, {cacertfile, "cacerts.pem"}, {reuse_sessions, false}, {server_name_indication, disable}]). {ok,{sslsocket,{gen_tcp,#Port<0.14>,tls_connection, ...}}
%% Fetch session ID and data for C9 connection 12> {ok, [{session_id, ID1}, {session_data, SessData}]} = ssl:connection_information(C9, [session_id, session_data]). {ok,[{session_id,<<9,233,4,54,170,88,170,180,17,96,202, 85,85,99,119,47,9,68,195,50,120,52, 130,239,...>>}, {session_data,<<131,104,13,100,0,7,115,101,115,115,105, 111,110,109,0,0,0,32,9,233,4,54,170,...>>}]}
%% Explicitly reuse the session from C9 13> {ok, C10} = ssl:connect("localhost", 9999, [{verify, verify_peer}, {versions, ['tlsv1.2']}, {cacertfile, "cacerts.pem"}, {reuse_session, {ID1, SessData}}]). {ok,{sslsocket,{gen_tcp,#Port<0.15>,tls_connection, undefined}, ...}}
14> ssl:connection_information(C10, [session_id]). {ok,[{session_id,<<9,233,4,54,170,88,170,180,17,96,202, 85,85,99,119,47,9,68,195,50,120,52, 130,239,...>>}]}
